  React.useEffect(() => {
    setState({
      $pos: checked ? uiVals.$checkedPos : uiVals.$uncheckedPos
    })
  }, [uiVals])

  function $onDragStart(clientX) {
    $inputRef.current.focus()
    // setState((oldState) => ({
    //   ...oldState,
    //   $startX: clientX,
    //   $hasOutline: true,
    //   $dragStartingTime: Date.now()
    // }))
    setState({
      $startX: clientX,
      $hasOutline: true,
      $dragStartingTime: Date.now()
    })
  }

  function $onDrag(clientX) {
    const { $startX, $isDragging, $pos } = state
    const startPos = checked ? uiVals.$checkedPos : uiVals.$uncheckedPos
    const mousePos = startPos + clientX - $startX
    // We need this check to fix a windows glitch where onDrag is triggered onMouseDown in some cases
    if (!$isDragging && clientX !== $startX) {
      // setState((oldState) => ({ ...oldState, $isDragging: true }))
      setState({ $isDragging: true })
    }
    const newPos = Math.min(
      uiVals.$checkedPos,
      Math.max(uiVals.$uncheckedPos, mousePos)
    )
    // Prevent unnecessary rerenders
    if (newPos !== $pos) {
      // setState((oldState) => ({ ...oldState, $pos: newPos }))
      setState({ $pos: newPos })
    }
  }

  function $onDragStop(event) {
    const { $pos, $isDragging, $dragStartingTime } = state
    const halfwayCheckpoint = (uiVals.$checkedPos + uiVals.$uncheckedPos) / 2

    /*
      Set position state back to the previous position even if user drags the switch with intention to change the state.
      This is to prevent the switch from getting stuck in the middle if the event isn't handled in the onChange callback.
    */
    const prevPos = checked ? uiVals.$checkedPos : uiVals.$uncheckedPos
    // setState((oldState) => ({ ...oldState, $pos: prevPos }))
    setState({ $pos: prevPos })

    // Act as if the user clicked the handle if they didn't drag it _or_ the dragged it for less than 250ms
    const timeSinceStart = Date.now() - $dragStartingTime
    const isSimulatedClick = !$isDragging || timeSinceStart < 250

    // Handle when the user has dragged the switch more than halfway from either side
    const isDraggedHalfway =
      (checked && $pos <= halfwayCheckpoint) ||
      (!checked && $pos >= halfwayCheckpoint)

    if (isSimulatedClick || isDraggedHalfway) {
      $onChange(event)
    }

    if ($isMounted.current) {
      // setState((oldState) => ({
      //   ...oldState,
      //   $isDragging: false,
      //   $hasOutline: false
      // }))
      setState({
        $isDragging: false,
        $hasOutline: false
      })
    }
    uiVals.$lastDragAt = Date.now()
  }

  function $onMouseDown(event) {
    event.preventDefault()
    // Ignore right click and scroll
    if (typeof event.button === 'number' && event.button !== 0) {
      return
    }

    $onDragStart(event.clientX)
    window.addEventListener('mousemove', $onMouseMove)
    window.addEventListener('mouseup', $onMouseUp)
  }

  function $onMouseMove(event) {
    event.preventDefault()
    $onDrag(event.clientX)
  }

  function $onMouseUp(event) {
    $onDragStop(event)
    window.removeEventListener('mousemove', $onMouseMove)
    window.removeEventListener('mouseup', $onMouseUp)
  }

  function $onTouchStart(event) {
    uiVals.$checkedStateFromDragging = null
    $onDragStart(event.touches[0].clientX)
  }

  function $onTouchMove(event) {
    $onDrag(event.touches[0].clientX)
  }

  function $onTouchEnd(event) {
    event.preventDefault()
    $onDragStop(event)
  }

  function $onInputChange(event) {
    // This condition is unfortunately needed in some browsers where the input's change event might get triggered
    // right after the dragstop event is triggered (occurs when dropping over a label element)
    if (Date.now() - uiVals.$lastDragAt > 50) {
      $onChange(event)
      // Prevent clicking label, but not key activation from setting outline to true - yes, this is absurd
      if (Date.now() - uiVals.$lastKeyUpAt > 50) {
        if ($isMounted) {
          // setState((oldState) => ({ ...oldState, $hasOutline: false }))
          setState({ $hasOutline: false })
        }
      }
    }
  }

  function $onKeyUp() {
    uiVals.$lastKeyUpAt = Date.now()
  }

  function $setHasOutline() {
    // setState((oldState) => ({ ...oldState, $hasOutline: true }))
    setState({ $hasOutline: true })
  }

  function $unsetHasOutline() {
    // setState((oldState) => ({ ...oldState, $hasOutline: false }))
    setState({ $hasOutline: false })
  }

  function $onClick(event) {
    event.preventDefault()
    $inputRef.current.focus()
    $onChange(event)
    if ($isMounted) {
      // setState((oldState) => ({ ...oldState, $hasOutline: false }))
      setState({ $hasOutline: false })
    }
  }

  function $onChange(event) {
    const { onChange, id } = rest
    onChange(!checked, event, id)
  }

  const { $pos, $isDragging, $hasOutline } = state

  const backgroundStyle = {
    height,
    width,
    margin: Math.max(0, (uiVals.$handleDiameter - height) / 2),
    position: 'relative',
    background: getBackgroundColor(
      $pos,
      uiVals.$checkedPos,
      uiVals.$uncheckedPos,
      offColor,
      onColor
    ),
    borderRadius: typeof borderRadius === 'number' ? borderRadius : height / 2,
    cursor: disabled ? 'default' : 'pointer',
    WebkitTransition: $isDragging ? null : 'background 0.25s',
    MozTransition: $isDragging ? null : 'background 0.25s',
    transition: $isDragging ? null : 'background 0.25s',
    ...backgroundStyleProp
  }

  const checkedIconStyle = {
    height,
    width: Math.min(
      height * 1.5,
      width - (uiVals.$handleDiameter + height) / 2 + 1
    ),
    position: 'relative',
    opacity:
      ($pos - uiVals.$uncheckedPos) /
      (uiVals.$checkedPos - uiVals.$uncheckedPos),
    pointerEvents: 'none',
    WebkitTransition: $isDragging ? null : 'opacity 0.25s',
    MozTransition: $isDragging ? null : 'opacity 0.25s',
    transition: $isDragging ? null : 'opacity 0.25s'
  }

  const uncheckedIconStyle = {
    height,
    width: Math.min(
      height * 1.5,
      width - (uiVals.$handleDiameter + height) / 2 + 1
    ),
    position: 'absolute',
    opacity:
      1 -
      ($pos - uiVals.$uncheckedPos) /
      (uiVals.$checkedPos - uiVals.$uncheckedPos),
    right: 0,
    top: 0,
    pointerEvents: 'none',
    WebkitTransition: $isDragging ? null : 'opacity 0.25s',
    MozTransition: $isDragging ? null : 'opacity 0.25s',
    transition: $isDragging ? null : 'opacity 0.25s'
  }

  const handleStyle = {
    height: uiVals.$handleDiameter,
    width: uiVals.$handleDiameter,
    background: getBackgroundColor(
      $pos,
      uiVals.$checkedPos,
      uiVals.$uncheckedPos,
      offHandleColor,
      onHandleColor
    ),
    display: 'inline-block',
    cursor: disabled ? 'default' : 'pointer',
    borderRadius: typeof borderRadius === 'number' ? borderRadius - 1 : '50%',
    position: 'absolute',
    transform: `translateX(${$pos}px)`,
    top: Math.max(0, (height - uiVals.$handleDiameter) / 2),
    outline: 0,
    boxShadow: $hasOutline ? activeBoxShadow : boxShadow,
    border: 0,
    WebkitTransition: $isDragging
      ? null
      : 'background-color 0.25s, transform 0.25s, box-shadow 0.15s',
    MozTransition: $isDragging
      ? null
      : 'background-color 0.25s, transform 0.25s, box-shadow 0.15s',
    transition: $isDragging
      ? null
      : 'background-color 0.25s, transform 0.25s, box-shadow 0.15s',
    ...handleStyleProp
  }

  const uncheckedHandleIconStyle = {
    height: uiVals.$handleDiameter,
    width: uiVals.$handleDiameter,
    opacity: Math.max(
      (1 -
        ($pos - uiVals.$uncheckedPos) /
        (uiVals.$checkedPos - uiVals.$uncheckedPos) -
        0.5) *
      2,
      0
    ),
    position: 'absolute',
    left: 0,
    top: 0,
    pointerEvents: 'none',
    WebkitTransition: $isDragging ? null : 'opacity 0.25s',
    MozTransition: $isDragging ? null : 'opacity 0.25s',
    transition: $isDragging ? null : 'opacity 0.25s'
  }

  const checkedHandleIconStyle = {
    height: uiVals.$handleDiameter,
    width: uiVals.$handleDiameter,
    opacity: Math.max(
      (($pos - uiVals.$uncheckedPos) /
        (uiVals.$checkedPos - uiVals.$uncheckedPos) -
        0.5) *
      2,
      0
    ),
    position: 'absolute',
    left: 0,
    top: 0,
    pointerEvents: 'none',
    WebkitTransition: $isDragging ? null : 'opacity 0.25s',
    MozTransition: $isDragging ? null : 'opacity 0.25s',
    transition: $isDragging ? null : 'opacity 0.25s'
  }

  const inputStyle = {
    border: 0,
    clip: 'rect(0 0 0 0)',
    height: 1,
    margin: -1,
    overflow: 'hidden',
    padding: 0,
    position: 'absolute',
    width: 1
  }

  const rootStyle = {
    position: 'relative',
    display: 'inline-block',
    textAlign: 'left',
    opacity: disabled ? 0.5 : 1,
    direction: 'ltr',
    borderRadius: height / 2,
    WebkitTransition: 'opacity 0.25s',
    MozTransition: 'opacity 0.25s',
    transition: 'opacity 0.25s',
    touchAction: 'none',
    WebkitTapHighlightColor: 'rgba(0, 0, 0, 0)',
    WebkitUserSelect: 'none',
    MozUserSelect: 'none',
    msUserSelect: 'none',
    userSelect: 'none'
  }

  return (
    <div className={className} style={rootStyle}>
      <div
        className='react-switch-bg'
        style={backgroundStyle}
        onClick={disabled ? null : $onClick}
        onMouseDown={(e) => e.preventDefault()}
      >
        {checkedIcon && <div style={checkedIconStyle}>{checkedIcon}</div>}
        {uncheckedIcon && <div style={uncheckedIconStyle}>{uncheckedIcon}</div>}
      </div>
      <div
        className='react-switch-handle'
        style={handleStyle}
        onClick={(e) => e.preventDefault()}
        onMouseDown={disabled ? null : $onMouseDown}
        onTouchStart={disabled ? null : $onTouchStart}
        onTouchMove={disabled ? null : $onTouchMove}
        onTouchEnd={disabled ? null : $onTouchEnd}
        onTouchCancel={disabled ? null : $unsetHasOutline}
      >
        {uncheckedHandleIcon && (
          <div style={uncheckedHandleIconStyle}>{uncheckedHandleIcon}</div>
        )}
        {checkedHandleIcon && (
          <div style={checkedHandleIconStyle}>{checkedHandleIcon}</div>
        )}
      </div>
      <input
        type='checkbox'
        role='switch'
        aria-checked={checked}
        checked={checked}
        disabled={disabled}
        style={inputStyle}
        {...rest}
        /* anything below should NOT get overriden by ...rest */
        ref={$inputRef}
        onFocus={$setHasOutline}
        onBlur={$unsetHasOutline}
        onKeyUp={$onKeyUp}
        onChange={$onInputChange}
      />
    </div>
  )
}

ReactSwitch.propTypes = {
  checked: PropTypes.bool.isRequired,
  onChange: PropTypes.func.isRequired,
  disabled: PropTypes.bool,
  offColor: hexColorPropType,
  onColor: hexColorPropType,
  offHandleColor: hexColorPropType,
  onHandleColor: hexColorPropType,
  handleDiameter: PropTypes.number,
  uncheckedIcon: PropTypes.oneOfType([PropTypes.bool, PropTypes.element]),
  checkedIcon: PropTypes.oneOfType([PropTypes.bool, PropTypes.element]),
  boxShadow: PropTypes.string,
  borderRadius: PropTypes.number,
  activeBoxShadow: PropTypes.string,
  uncheckedHandleIcon: PropTypes.element,
  checkedHandleIcon: PropTypes.element,
  height: PropTypes.number,
  width: PropTypes.number,
  id: PropTypes.string,
  className: PropTypes.string
}

ReactSwitch.defaultProps = {
  disabled: false,
  offColor: '#888',
  onColor: '#080',
  offHandleColor: '#fff',
  onHandleColor: '#fff',
  uncheckedIcon: defaultUncheckedIcon,
  checkedIcon: defaultCheckedIcon,
  boxShadow: null,
  activeBoxShadow: '0 0 2px 3px #3bf',
  height: 28,
  width: 56
}

export default ReactSwitch
